import React, { useState } from "react";
import "./App.css";

import completed from './completed.png';



export default function ToDoListContainer() {
  const [inputValue, setInputValue] = useState("");
  const [todoList, setTodoList] = useState([]);

  const handleAddButtonClick = () => {
    if (inputValue.trim() !== "") {
      setTodoList([inputValue, ...todoList]);
      setInputValue("");
    }
  };

  const handleDeleteBtnClick = (index) => {
    const updatedList = [...todoList];
    updatedList.splice(index, 1);
    setTodoList(updatedList);
  };

  const handleDoneBtnClick = (index) => {
    const updatedList = [...todoList];
    updatedList[index].completed = true;
    setTodoList(updatedList);
  };


  return (
    <div className="main">
      <div className="container">
        <h1 className="title">To Do List</h1>
        <nav className="navigationBar">
    <div className="searchDiv">
    <input type="text" className="searchInput" placeholder="Search..." />
    <button className="searchBtn"></button>
    </div>
        </nav>
      </div>
      <div className="inputDiv">
        <input
          className="inputField"
          type="text"
          name="inputField"
          placeholder="Dream, Plan, Do!"
          required
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button className="submitBtn" onClick={handleAddButtonClick}>
          Add
        </button>
      </div>
      <div className="sec-main">
        {todoList.map((task, index) => (
          <div className="todoitem" key={index}>
            <img className="completedIcon" src={completed} alt="completed icon" />
            <p className="todo-text">{task}</p>
            <div className="btnDiv">
              <button
                onClick={() => handleDeleteBtnClick(index)}
                className="deleteBtn"
              >
                <i className="fas fa-minus white-icon"></i>
              </button>

              <button
                className="doneBtn"
              >
                <i className="fas fa-check white"></i>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
