import React, { useState, useEffect } from "react";
import "./App.css";
import editedIcon from './icon.png';
import icon2 from './icon2.png';
import icon3 from './icon3.png';
import completed from './icon4.png';

export default function ToDoListContainer() {
  const [inputValue, setInputValue] = useState("");
  const [todoList, setTodoList] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    const storedTodoList = localStorage.getItem("todoList");
    if (storedTodoList) {
      setTodoList(JSON.parse(storedTodoList));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("todoList", JSON.stringify(todoList));
  }, [todoList]);

  const handleAddButtonClick = () => {
    if (inputValue.trim() !== "") {
      setTodoList([...todoList, { task: inputValue, completed: false, editing: false }]);
      setInputValue("");
    }
  };

  const handleDeleteBtnClick = (index) => {
    const updatedList = [...todoList];
    updatedList.splice(index, 1);
    setTodoList(updatedList);
  };

  const handleDoneBtnClick = (index) => {
    const updatedList = [...todoList];
    updatedList[index].completed = true;
    setTodoList(updatedList);
  };

  const handleEditInputChange = (event, index) => {
    const updatedList = [...todoList];
    updatedList[index].task = event.target.value;
    setTodoList(updatedList);
  };

  const handleEditBtnClick = (index) => {
    const updatedList = [...todoList];
    updatedList[index].editing = !updatedList[index].editing;
    setTodoList(updatedList);
  };

  const filteredTodoList = todoList.filter((task) =>
    task.task.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="main">
      <div className="container">
        <h1 className="title">To Do List</h1>
        <nav className="navigationBar">
          <div className="searchDiv">
            <input
              type="text"
              className="searchInput"
              placeholder="Search..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button className="searchBtn"></button>
          </div>
        </nav>
      </div>
      <div className="inputDiv">
        <input
          className="inputField"
          type="text"
          name="inputField"
          placeholder="Dream, Plan, Do!"
          required
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button className="submitBtn" onClick={handleAddButtonClick}>
          Add
        </button>
      </div>
      <div className="sec-main">
        {filteredTodoList.map((task, index) => (
          <div className="todoitem" key={index}>
            {!task.editing && (
              <img
                onClick={() => handleEditBtnClick(index)}
                title="edit"
                className="editedicon"
                src={editedIcon}
                alt="icon"
              />
            )}
                 {task.completed && (
          <img className="completedIcon" src={completed} alt="completed icon" />
        )}
        {!task.editing ? (
          <p className="todo-text">{task.task}</p>
        ) : (
          <input
            className="editinput"
            type="text"
            value={task.task}
            onChange={(event) => handleEditInputChange(event, index)}
          />
        )}

        <div className="btnDiv">
        {!task.editing && !task.completed && (
        <img
          title="completed"
          src={icon2}
         onClick={() => handleDoneBtnClick(index)}
         />
         )}
         {!task.editing && (
          <img
            src={icon3}
            alt="icon"
            title="Delete"
            onClick={() => handleDeleteBtnClick(index)}
            className="deleteBtn"
          />
         )}
        </div>
      </div>
    ))}
  </div>
</div>
   );
 }
