import React, { useState } from "react";
import './App.css';
import CompleteBtn from "./completeBtn";
import SearchBtn from "./searchBtn";
import ReminderBtn from "./reminder";
import LabelBtn from "./label";


export default function ToDoListContainer(){
    const [inputValue, setInputValue] = useState("");
    const [todoText, setTodoText] = useState("");

    const handleAddButtonClick = () => {
        setTodoText(inputValue);
    };

    return (
        <div className="main">
            <div className="container">
                <h1 className="title">To Do List</h1>
                <nav className="navigationBar">
                    <CompleteBtn />
                    <SearchBtn />
                    <ReminderBtn />
                    <LabelBtn />
                </nav>
            </div>
            <div className="inputDiv">
                <input
                    className="inputField"
                    type="text" 
                    name="inputField"  
                    placeholder="Dream, Plan, Do!"  
                    required
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                />
                <button className="submitBtn" onClick={handleAddButtonClick}>Add</button>
            </div>
            <p className="todo-text">{todoText}</p>
        </div>
    );
}
