import React, { useState } from "react";
import './App.css';
import CompleteBtn from "./completeBtn";
import SearchBtn from "./searchBtn";
import ReminderBtn from "./reminder";
import LabelBtn from "./label";
import labelIcon from './label.png';
import labelIcon2 from './label2.png';
import labelIcon3 from './label3.png';
import labelIcon4 from './label4.png';
import labelIcon6 from './label6.png';
import labelIcon7 from './label7.png';

export default function ToDoListContainer() {
  const [inputValue, setInputValue] = useState("");
  const [todoList, setTodoList] = useState([]);
  const [completedList, setCompletedList] = useState([]);
  const [isCompleteButtonClicked, setCompleteButtonClicked] = useState(false);

  const handleAddButtonClick = () => {
    if (inputValue.trim() !== "") {
      setTodoList([inputValue, ...todoList]);
      setInputValue("");
    }
  };

  const handleDeleteBtnClick = (index) => {
    const updatedList = [...todoList];
    const deletedItem = updatedList.splice(index, 1);
    setTodoList(updatedList);
    setCompletedList([deletedItem, ...completedList]);
  };

  const handleCompleteBtnClick = () => {
    setCompleteButtonClicked(!isCompleteButtonClicked);
  };

  const handleDoneBtnClick = (index) => {
    const updatedList = [...todoList];
    const completedItem = updatedList.splice(index, 1);
    setTodoList(updatedList);
    setCompletedList([completedItem, ...completedList]);
  };

  return (
    <div className="main">
      <div className="container">
        <h1 className="title">To Do List</h1>
        <nav className="navigationBar">
          <CompleteBtn onClick={handleCompleteBtnClick} />
          <SearchBtn />
          <ReminderBtn />
          <LabelBtn />
        </nav>
      </div>
      <div className="inputDiv">
        <input
          className="inputField"
          type="text"
          name="inputField"
          placeholder="Dream, Plan, Do!"
          required
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button className="submitBtn" onClick={handleAddButtonClick}>
          Add
        </button>
      </div>
      <div className="sec-main">
        {isCompleteButtonClicked
          ? completedList.map((todo, index) => (
              <div className="todoitem" key={index}>
                <div className="labelDiv">
                  <img style={{ display: "none" }} src={labelIcon} alt="Label icon" />
                  <img style={{ display: "none" }} src={labelIcon2} alt="Label2 icon" />
                  <img style={{ display: "none" }} src={labelIcon3} alt="Label3 icon" />
                  <img style={{ display: "none" }} src={labelIcon4} alt="Label4 icon" />
                  <img src={labelIcon6} alt="Label6 icon" />
                  <img style={{ display: "none" }} src={labelIcon7} alt="Label7 icon" />
                </div>
                <p className="todo-text">{todo}</p>
                <div className="btnDiv">
                  <button
                    onClick={() => handleDeleteBtnClick(index)}
                    className="deleteBtn"
                  >
                    <i className="fas fa-minus white-icon"></i>
                  </button>
                  <button className="doneBtn" onClick={() => handleDoneBtnClick(index)}>
                    <i className="fas fa-check white"></i>
                  </button>
                </div>
              </div>
            ))
          : todoList.map((todo, index) => (
              <div className="todoitem" key={index}>
                <div className="labelDiv">
                  <img style={{ display: "none" }} src={labelIcon} alt="Label icon" />
                  <img style={{ display: "none" }} src={labelIcon2} alt="Label2 icon" />
                  <img style={{ display: "none" }} src={labelIcon3} alt="Label3 icon" />
                  <img style={{ display: "none" }} src={labelIcon4} alt="Label4 icon" />
                  <img src={labelIcon6} alt="Label6 icon" />
                  <img style={{ display: "none" }} src={labelIcon7} alt="Label7 icon" />
                </div>
                <p className="todo-text">{todo}</p>
                <div className="btnDiv">
                  <button
                    onClick={() => handleDeleteBtnClick(index)}
                    className="deleteBtn"
                  >
                    <i className="fas fa-minus white-icon"></i>
                  </button>
                  <button className="doneBtn" onClick={() => handleDoneBtnClick(index)}>
                    <i className="fas fa-check white"></i>
                  </button>
                </div>
              </div>
            ))}
      </div>
    </div>
  );
}
